def optimizeL2(M: Mat, K: Int, iters: Int, alpha:Double = 0.1, initScale:Double = 1.0): (Seq[Vect], Seq[Vect]) = {
  val rand = new scala.util.Random(0)
  val AV = initialAV(K, M.dim1, M.dim2, initScale)
  val A = AV._1; val V = AV._2
  def update(i: Int, j: Int) = {
    val a = A(i).copy
    val v = V(j).copy
    val y = a dot v
    A(i) += v * alpha * (M(i, j) - y)
    V(j) += a * alpha * (M(i, j) - y)
  }
  for (i <- Range(0,iters).toList) {
    update(rand.nextInt(M.dim1), 
           rand.nextInt(M.dim2))
  }
  (A, V)
}

def optimizeLogistic(M: Mat, K: Int, iters: Int, alpha:Double = 0.1, initScale:Double = 1.0): (Seq[Vect], Seq[Vect]) = {
  val rand = new scala.util.Random(0)
  val AV = initialAV(K, M.dim1, M.dim2, initScale)
  val A = AV._1; val V = AV._2
  def update(i: Int, j: Int) = {
    val a = A(i).copy
    val v = V(j).copy
    val p = sigmoid(a dot v)
    A(i) += v * alpha * (M(i, j) - p)
    V(j) += a * alpha * (M(i, j) - p)
  }
  for (i <- Range(0,iters).toList) {
    update(rand.nextInt(M.dim1), 
           rand.nextInt(M.dim2))
  }
  (A, V)
}
